import React, { useState } from 'react';
import XLSX from 'xlsx/dist/xlsx.full.min';

const FileUpload = ({ onFileUpload, searchString }) => {
  const handleFileChange = async (e) => {
    const uploadedFile = e.target.files[0];

    const reader = new FileReader();
    reader.onload = async (e) => {
      const data = new Uint8Array(e.target.result);
      const workbook = XLSX.read(data, { type: 'array' });

      const analyzedData = analyzeExcelData(workbook, searchString); // Pass searchString here
      onFileUpload(analyzedData);
    };
    reader.readAsArrayBuffer(uploadedFile);
  };

  const analyzeExcelData = (workbook, searchString) => {
    const analyzedResults = {
      Kanban: 0,
      'In Process': 0,
      Failed: 0,
    };

    const sheetName = workbook.SheetNames[0];
    const sheet = workbook.Sheets[sheetName];
    const rows = XLSX.utils.sheet_to_json(sheet, { header: 1 });

    for (const row of rows) {
      const pn = row['PN'];
      const lastStation = row['Last Station'];
      const returned = row['Returned'];
      const woType = row['Wo Type'];
      const condition = row['Condition'];

      if (pn && lastStation && returned !== 0 && woType === 'ARRIBA') {
        if (lastStation === 1261 || lastStation === 1181) {
          analyzedResults.Kanban += 1;
        } else if (condition === 'Fail') {
          analyzedResults.Failed += 1;
        } else if (condition === 'Pass') {
          analyzedResults['In Process'] += 1;
        }
      }
    }

    return analyzedResults;
  };


  return (
    <div className="file-upload">
      <input type="file" onChange={handleFileChange} accept=".xlsx" />
    </div>
  );
};

export default FileUpload;
